import React from "react";
import M from "materialize-css";
import FileUpload from "./fileUpload";
import firebase from "firebase/app";
import "firebase/database";
import "firebase/firebase-storage";

class Modal extends React.Component {

  state = {
    user: {
      photoURL: "",
      displayName: "",
    },
    uploadValue: 0,
  };

  componentDidMount() {
    M.AutoInit();
    firebase.auth().onAuthStateChanged(user =>{
      this.setState({user})
    })
  }
  handleChange = (e) =>{
    this.setState({
      txt: e.target.value
    })
  }

  handleUpload = (e) =>{
    e.preventDefault()
    const record = {
      avatar: this.state.user.photoURL,
      nombre: this.state.user.displayName,
      txt: this.state.txt,
      pic: "",
    };

    const file = e.target.files[0];
    const storageRef = firebase.storage().ref(`fotos/${file.name}`);
    const task = storageRef.put(file);

    task.on("state_changed", snapshot =>{
      let percentage = (snapshot.bytesTransferred/snapshot.totalBytes) * 100;
      this.setState({
        uploadValue: percentage
      })
    })

    const db = firebase.database();
    const dbRef = db.ref("pictures");
    const newPicture = dbRef.push();
    newPicture.set(record);

  }

  render() {
    return (
      <React.Fragment>
        <a
          href="#modal1"
          className="btn-floating btn-large waves-effect waves-light modal-trigger red FAB"
          id="fab"
        >
          <i className="material-icons">add</i>
        </a>

        <div id="modal1" className="modal">
          <div className="modal-content">
            <h4>Poste√° en Truchigram</h4>
            <div className="chip">
              <img src={this.state.user.photoURL} />
              {this.state.user.displayName}
            </div>

            <div className="row">
              <form className="col l12">
                <div className="row">
                  <div className="input-field col s9 l4">
                    <i className="material-icons prefix">mode_edit</i>
                    <textarea
                      id="icon_prefix2"
                      className="materialize-textarea"
                      onChange={this.handleChange}
                    />
                    <label htmlFor="icon_prefix2">Mensaje</label>
                    <FileUpload onUpload={this.handleUpload} uploadValue={this.state.uploadValue} />

                  </div>
                </div>
              </form>
            </div>
          </div>
          <div className="modal-footer" />
        </div>
      </React.Fragment>
    );
  }
}

export default Modal;
